import emptyBpmn from './empty.bpmn';

export default class BPMNContainer extends React.Component {
  componentDidMount() {
    var modeler = new BpmnModeler({
      container: '#bpmnview'
    });
    this.openDiagram(emptyBpmn);
  }
  render() {
    return (
      <React.Fragment>
        <div id="bpmnview"></div>
        <style jsx>
          {`
            #bpmnview {
              height: 100vh;
            }
          `}
        </style>
      </React.Fragment>
    );
  }
}
